import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Sidebar from './components/Sidebar/Sidebar';
import ToggleButton from './components/ToggleButton/ToggleButton';
import Header from './components/Header/Header';
import Dashboard from './components/Dashboard-transport/Dashboard';
import DriverProfile from './components/DriverProfile/DriverProfile'; // Ensure this component exists
import DriversList from './components/DriversList-transport/DriversList';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';

function App() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <Router>
      <div className="app">
        {/* Sidebar Component */}
        <Sidebar isOpen={isSidebarOpen} />

        <div className={`content ${isSidebarOpen ? "sidebar-open" : ""}`}>
          <header className="header">
            <ToggleButton toggleSidebar={toggleSidebar} />
            <Header />
          </header>

          {/* Main Content */}
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/drivers" element={<DriversList />} />
            {/* :driverId is a URL parameter, for individual driver profiles */}
            <Route path="/drivers/:driverId" element={<DriverProfile />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
