import React, { useState } from 'react';
import './VehicleProfile.css';

const VehicleProfile = () => {
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [mapUrl, setMapUrl] = useState('');

  const handleRouteClick = () => {
    if (startLocation && endLocation) {
      const mapUrl = `https://www.google.com/maps/embed/v1/directions?key=YOUR_GOOGLE_MAPS_API_KEY&origin=${startLocation}&destination=${endLocation}&mode=driving`;
      setMapUrl(mapUrl);
    }
  };

  return (
    <div className="vehicle-profile-container">
      <div className="vehicle-details">
        <h2>Vehicle Profile</h2>
        <div className="vehicle-info">
          <p>Bus No 1</p>
          <p>Model: JCBL Happy Bus</p>
          <p>Current Driver: Alfonso Bator</p>
          <p>Vehicle Status: In Use</p>
          <p>Last Maintenance: Aug 30, 2024</p>
        </div>

        <div className="map-container">
          <iframe
            width="100%"
            height="450"
            frameBorder="0"
            style={{ border: 0 }}
            src={mapUrl || "https://www.google.com/maps/embed/v1/view?key=YOUR_GOOGLE_MAPS_API_KEY&center=40.730610,-73.935242&zoom=12"}
            allowFullScreen
          ></iframe>

          <div className="route-inputs">
            <input
              type="text"
              placeholder="Enter Start Location"
              value={startLocation}
              onChange={(e) => setStartLocation(e.target.value)}
            />
            <input
              type="text"
              placeholder="Enter End Location"
              value={endLocation}
              onChange={(e) => setEndLocation(e.target.value)}
            />
            <button onClick={handleRouteClick}>Set Route</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default VehicleProfile;
